import React, { useState } from 'react';
import './cardBoard.css';
import Card from './card.js';
import getRandomCardsOrder from './RandomCardsNum';


const CardsBoard = (props) => {

    const [wasClicked, setCardClickedState] = useState('false');
    //const [disable, setCardAsDisable] = useState(props.disable);

    const clickCardHandler = () => {
        setCardClickedState('true');
        //console.log(wasClicked);
        console.log('Updated!');
    }
    
    const characters = props.items;

    const charactersSorted = getRandomCardsOrder(characters, 12);
    for (let i=0; i<12; i++){
        charactersSorted.splice(i, 1, {id: i, character: charactersSorted[i]});
    }

    return (<div className='board'>
            {charactersSorted.map((element) => (<Card id={element.id} character={element.character} clicked={wasClicked} disable={true} onclick={clickCardHandler}></Card>))}
        <div className='score'>Turn counter: 0</div>
    </div>)
}


export default CardsBoard;
