import React, { useState } from 'react';
import './card.css'
import CardImg from './cardImage';

const Card = (props) => {
    
    let activeCharacter = props.character;
    const [wasClicked, setCardClickedState] = useState(true);
    const [disable, setCardAsDisable] = useState(props.disable);

    const clickCardHandler = () => {        
        setCardClickedState(!wasClicked);        
        props.clickedCard(wasClicked);
    }

    /* const activationCardHandler = () => {
        wasClicked ? setCardAsDisable(!disable);
    }
 */
    return <div className={wasClicked ? 'card' : 'cardRevealed'} onClick={clickCardHandler}><CardImg id={props.key} activeCard={activeCharacter} isActive={wasClicked ? true : false}/></div>
    
}

export default Card;
