import React, { useState } from 'react';
import './cardBoard.css';
import Card from './card.js';
import getRandomCardsOrder from './RandomCardsNum';

/* const [name, setCardName] = useState(

);
 */
const CardsBoard = (props) => {

    const cardName = [];
    const cardName2 = [];

    for (let item of props.items){
        cardName[item]= props.items[item];
    }

    cardName2 = getRandomCardsOrder(cardName, 6);

    return (<div className='board'>
        <Card itemName={cardName[0]}></Card>
        <Card></Card>
        <Card></Card>
        <Card></Card>
        <Card></Card>
        <Card></Card>
        <Card></Card>
        <Card></Card>
        <Card></Card>
        <Card></Card>
        <Card></Card>
        <Card></Card>
        <div className='score'>Turn counter: 0</div>
    </div>)
}


export default CardsBoard;
