import React, { useState } from 'react';
import './card.css'
import hogward from './img/hogwards.jpg'
import harry from './img/harry.jpg'
import ron from './img/ron.jpg'
import hermione from './img/hermione.jpg'
import dumpledore from './img/dumbledore.jpg'
import voldemort from './img/voldemort.jpg'
import dobby from './img/dobby.jpg'
import CardImg from './cardImage';

const Card = (props) => {
    /* const IMG ={
        school: require.context('./img/hogwards.jpg').default,
    } */
    const defaulfImage = './img/hogwards.jpg';
    let image = './img/' + props.character;
    //let visibleCardSide = defaulfImage;
    let visibleCardSide = hogward;
    let clasa = 'card';

    const [wasClicked, setCardClickedState] = useState(true);
    //const [disable, setCardAsDisable] = useState(props.disable);

    const clickCardHandler = () => {
        console.log(image[0]);
        setCardClickedState(!wasClicked);
        console.log('Updated!', wasClicked);
        props.clickedCard(wasClicked);
        //revealCard(wasClicked);
        clasa = 'cardRevealed';
    }

    const revealCard = () => {
        wasClicked ? (visibleCardSide = hogward) : (visibleCardSide = harry);
        //console.log(visibleCardSide);
        //document.documentElement.style.setProperty('--new-image', 'url("img/harry.jpg")')
        //return <img src={harry}></img>;
    }

    return <div className={wasClicked ? 'card' : 'cardRevealed'} onClick={clickCardHandler}></div>
    //return <div className={wasClicked ? 'card' : 'cardRevealed'} onClick={clickCardHandler} style={{backgroundImage : {revealCard}}}></div>
    //return <div className={wasClicked ? 'card' : 'cardRevealed'} onClick={clickCardHandler} style={{backgroundImage : `url(${visibleCardSide})`}}></div>
   
}

export default Card;
