                    break;
                case 'voldemort':
                    image = voldemort; 
                    break;
                case 'dobby':
                    image = dobby; 
                    break;
                default:
                    image = hogward;
            }
        }
        else
        {
            if (block==false) {
                image = hogward;
                block = true;
            }
                
        }
            //console.log('block', block);
    }
    changeStateHangler();

